mapper.update:
update tbl_board
set view_count = view_count + 1
where board_id = #{boardId}

update tbl_board
set has_attach = #{hasAttach}
where board_id = #{boardId}

update tbl_board
set thumbnail_uri = #{thumbnailUri}
where board_id = #{boardId}

mapper.insert:
insert
into tbl_board (admin_id, category_id, title, content)
values(#{adminId}, #{categoryId}, #{title}, #{content});
