mapper.insert:
insert
into tbl_faq (admin_id, category_id, title, content)
values(#{adminId}, #{categoryId}, #{title}, #{content});

mapper.update:
update tbl_faq
set thumbnail_uri = #{thumbnailUri}
where faq_id = #{faqId}

update tbl_faq
set has_attach = #{hasAttach}
where faq_id = #{faqId}

mapper.select:
select f.faq_id, f.title, f.content, f.register_date, f.update_date, f.category_id, ct.name category_name from tbl_faq as f join tbl_category ct on f.category_id = ct.category_id
where f.category_id = #{categoryId}
order by f.register_date desc, f.faq_id desc
