mapper.select:
select board_id
  from board
 where board_id = #{boardId} and guest_password = #{guestPassword}

mapper.update:
update board
   set board_view_count = board_view_count + 1
 where board_id = #{boardId}

update board
   set board_has_attach = #{boardHasAttach}
 where board_id = #{boardId}

update board
   set guest_name = #{guestName}
     , board_title = #{boardTitle}
     , board_content = #{boardContent}
     , board_update_date = now()
 where board_id = #{boardId}

mapper.insert:
insert
  into board (category_id, board_title, board_content, guest_name, guest_password)
values(#{categoryId}, #{boardTitle}, #{boardContent}, #{guestName}, #{guestPassword});
